mapper.select:
SELECT a.id AS "id", a.head_img AS "headImg", a.name AS "name", a.phone AS "phone", a.company_id AS "companyId", a.is_on_duty AS "isOnDuty", a.plate_number AS "plateNumber", a.last_sign_date AS "lastSignDate", c.name as "companyName", (SELECT case when sum(e.arrive_sign_quantity) is null THEN 0 ELSE sum(e.arrive_sign_quantity) END FROM bill_delivery_sign e WHERE e.delivery_bill_no=#{deliveryBillNo}) AS "arriveSign"
,b.delivery_bill_no AS "delivery.deliveryBillNo", b.lading_bill_no AS "delivery.ladingBillNo", b.status AS "delivery.status", b.sign_quantity AS "delivery.signQuantity", b.sign_date AS "delivery.signDate", b.plate_number AS "delivery.plateNumber", b.create_date AS "delivery.createDate", b.shipper_name AS "delivery.shipperName", b.start_delivery_date AS "delivery.startDeliveryDate", b.end_delivery_date AS "delivery.endDeliveryDate", d.take_mode AS "billLading.takeMode", d.bill_date AS "billLading.billDate", d.storehouse_type AS "billLading.storehouseType", d.customer_name AS "billLading.customerName", d.customer_contact AS "billLading.customerContact", d.customer_phone AS "billLading.customerPhone", d.delivery_address AS "billLading.deliveryAddress", d.storage_name AS "billLading.storageAddress", d.materiel_name AS "billLading.materielName", d.delivery_lng AS "billLading.deliveryLng", d.delivery_lat AS "billLading.deliveryLat", d.arrive_lng AS "billLading.arriveLng", d.arrive_lat AS "billLading.arriveLat"
/*(CASE WHEN b.status = 1 THEN '已分配' WHEN b.status = 2 THEN '运输中' WHEN b.status = 3 THEN '已完成' WHEN b.status = 4 THEN '中途停车' WHEN b.status = 5 THEN '运输完成' END ) AS 'label'*/
FROM base_driver a
LEFT JOIN (SELECT * FROM bill_delivery WHERE delivery_bill_no = #{deliveryBillNo}) b ON a.id=b.driver_id LEFT JOIN bill_lading d ON b.lading_bill_no=d.lading_bill_no
LEFT JOIN base_company c ON a.company_id=c.id WHERE a.id=#{driverId}

SELECT 

FROM base_driver a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE base_driver SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT

FROM base_driver a


	a.del_flag = #{DEL_FLAG_NORMAL}
	AND
	a.status = 0

		AND  a.company_id = #{companyId}


		AND a.company_id IS NULL




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT a.id, a.head_img AS 'headImg', a.name AS 'name', b.name AS 'companyName', a.plate_number AS 'plateNumber', a.is_on_duty AS 'isOnDuty' FROM base_driver a
a.del_flag = #{DEL_FLAG_NORMAL} and a.status=1
AND a.company_id=${companyId}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC
